import React ,{ useState } from "react";
import { View, SafeAreaView, TouchableOpacity, Text } from 'react-native';
import { InputBusca } from "../components/InputBusca";
import { Styles } from "../components/Styles";
import { TypeText } from "../components/TypeText";
import { ButtonOne } from "../components/ButtonOne";
import { FormInput } from "../components/FormInput";

export function Pagamento(){

    const [pos, setPos] = useState(0);

    const onPress = () => setPos(pos == 0? prevPos => prevPos + 1 : prevPos => prevPos - 1);

    return(
        <View style = {{flex: 1}}>
            <SafeAreaView style = {{flex:1, marginHorizontal: 25, marginVertical: 50}}>
                <View>
                    <TypeText textTitle = 'Adicionar Pagamento'/>
                </View>

                <View style = {{flex:1, justifyContent:'center'}}>
                    <InputBusca buscaTitulo = 'Buscar Cliente' image = {true} />

                    <View style = {{marginTop: 20}}><InputBusca buscaTitulo = 'Valor'/></View>

                    <View style = {{marginTop: 118, alignItems: 'center'}}>
                        <ButtonOne title = 'Salvar'/>
                    </View>
                </View>         
            </SafeAreaView>
        </View>
    );
}
