import * as React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, LogBox } from 'react-native';
import LogIn from './src/screens/LogIn';
import Home from './src/screens/Home';
import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useFonts } from 'expo-font';
import { Data } from './src/screens/Data';
import { Escolha } from './src/screens/Escolha';
import { AddCliente } from './src/screens/AddCliente';
import { AddProduto } from './src/screens/AddProduto';
import { BuscarCliente } from './src/screens/BuscarCliente';
import { Pagamento } from './src/screens/Pagamento';
import { Config } from './src/screens/Config';
import { ClienteData } from './src/screens/ClienteData';
import { Consulta } from './src/screens/Consulta';
import Cadastrar from './src/screens/Cadastrar';
import { EditarCliente } from './src/screens/EditarCliente';
import { EditarProduto } from './src/screens/EditarProduto';
import AuthProvider from './src/context/AuthContext';

export default function App() {

  const [loaded] = useFonts({
    Archivo: require('./assets/fonts/Archivo-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }

  const Stack = createNativeStackNavigator();

  const MyTheme = {
    ...DefaultTheme,
    colors: {
      ...DefaultTheme.colors,
      background: '#E7DCF2', 
    },
  };

  LogBox.ignoreLogs(['Setting a timer']);

  return (
    <NavigationContainer theme = {MyTheme}>

      <AuthProvider>

        <Stack.Navigator
            initialRouteName="LogIn"
            screenOptions={{
              headerShown: false,
              gestureEnabled: true,
            }}
          >

            <Stack.Screen
              name="LogIn"
              component={LogIn}
            />

            <Stack.Screen
              name="Home"
              component={Home}
            />

            <Stack.Screen
              name="Cadastrar"
              component={Cadastrar}
            />

            <Stack.Screen
              name="Consulta"
              component={Consulta}
            />  

            <Stack.Screen
              name="Escolha"
              component={Escolha}
            />  

            <Stack.Screen
              name="BuscarCliente"
              component={BuscarCliente}
            />  

            <Stack.Screen
              name="ClienteData"
              component={ClienteData}
            /> 

            <Stack.Screen
              name="EditarCliente"
              component={EditarCliente}
            />

            <Stack.Screen
              name="EditarProduto"
              component={EditarProduto}
            />

            <Stack.Screen
              name="AddCliente"
              component={AddCliente}
            />

            <Stack.Screen
              name="AddProduto"
              component={AddProduto}
            />  

            <Stack.Screen
              name="Config"
              component={Config}
            /> 
          </Stack.Navigator>

        </AuthProvider>
    </NavigationContainer>
  );
}

