import React ,{ useState, useContext, useEffect } from "react";
import { View, Text, Image, SafeAreaView, TouchableOpacity } from 'react-native';
import { Styles } from "../components/Styles";
import {FormData} from "../components/FormData";
import {FormVendas} from "../components/FormVendas";
import { FormEnd } from "../components/FormEnd";
import { db } from "../util/Firebase";
import { auth } from "../util/Firebase";
import { AuthContext } from "../context/AuthContext";

function Home({navigation, route}){

    const { user } = useContext(AuthContext);
    const [Nome, setUsuario] = useState('');
    const [ClientesLista, setClientesLista] = useState(null);
    const [Lucro, setLucro] = useState(null);

    function screenAdd(){
        navigation.navigate('Escolha');
    }

    function consultar(){
        navigation.navigate("Consulta");
    }

    function config(){
        navigation.navigate("Config",{usuario: Nome});
    }

    useEffect(() => {
        /* let list = [];
        async function loadClientes() {
            await db.collection('Usuários').doc(user.uid).collection('Clientes').get().then(querySnapshot => {
                querySnapshot.docs.forEach((doc) => {
                    list.push(doc.data().Nome)
                });
            });
            setClientesLista(list);
        }
        loadClientes(); */

        /* db.doc('Usuários/' + user.uid).get().then( // percorre o bd
            doc=>{
              if(doc.exists){ // verifica se existe o que foi buscado
                setUsuario(doc.data().NomeUsuario);
                setLucro(doc.data().Lucro);
              }
            }
        ); */

        db.collection('Usuários').doc(user.uid).onSnapshot((doc) => {
            setUsuario(doc.data().NomeUsuario);
            setLucro(doc.data().Lucro);       
        });


    }, []);


    return(
        <View style = {{flex:1}}>
            <SafeAreaView style = {{flex:1, marginHorizontal: 25, marginVertical: 50}}>
                <View>
                    <View style = {{flexDirection: "row", justifyContent:'space-between', alignItems:'center'}} >
                        <Text style = {Styles.text2}>{Nome}</Text>
                        <Image source = {require('../../assets/icons/PerfilTwo.png')} style={{ width: 40, height: 40 }} />
                    </View>

                    <View >                     
                        <FormData text = 'Balanço' value = {Lucro} />                  
                    </View>
                </View>

                <View style = {{flex:1, marginVertical: 35}}>
                    <View style = {{flexDirection: 'row', justifyContent:'space-between'}}>
                        <TouchableOpacity onPress={screenAdd}>
                            <View style={{ alignItems: 'center' }}>
                                <Image source = {require('../../assets/icons/add.png')} style={{ width: 50, height: 50 }} />
                                <Text style = {{ width: 100, textAlign: 'center', color: '#734D8C' }}>Adicionar Venda</Text>
                            </View>
                        </TouchableOpacity>

                        <TouchableOpacity>
                            <View style={{ alignItems: 'center' }}>
                                <Image source = {require('../../assets/icons/pagamento.png')} style={{ width: 50, height: 50 }} />
                                <Text style = {{ width: 100, textAlign: 'center', color: '#734D8C' }}>Adicionar Pagamento</Text>
                            </View>
                        </TouchableOpacity>

                        <TouchableOpacity onPress={consultar}>
                            <View style={{ alignItems: 'center' }}>
                                <Image source = { require('../../assets/icons/clientes.png')} style={{ width: 50, height: 50 }} />
                                <Text style = {{ width: 100, textAlign: 'center', color: '#734D8C' }}>Consultar Clientes</Text>
                            </View>
                        </TouchableOpacity>
                    </View>

                    <View style = {{flex:1, marginVertical: 35}}>
                        <Text style = {Styles.text3}>Últimas Vendas</Text>

                        <View>
                            <FormVendas cliente = 'Luana' compra = 'compra short' preco = 'R$ 50' />
                        </View>

                        <View>
                            <FormVendas cliente = 'Luana' compra = 'compra short' preco = 'R$ 50' />
                        </View>

                        <View>
                            <FormVendas cliente = 'Luana' compra = 'compra short' preco = 'R$ 50' />
                        </View>
                    </View>
                </View>

                <FormEnd home = {true} config = {false} dados = {false} onPress = {config}/>
            </SafeAreaView>
        </View>
    );
} 

export default Home;
